var start = new Date();
var end = new Date();

switch (currentPatient.Age_val) {
    case "0":
        start.setMonth(start.getMonth() - 2);
        end = '';
        break;
    case "1":
        start.setMonth(start.getMonth() - 12);
        end.setMonth(end.getMonth() - 2);
        break;
