        start.setMonth(start.getMonth() - 60);
        end.setMonth(end.getMonth() - 25);
        break;
    case "4":
        start = '';
        end.setMonth(end.getMonth() - 60);
        break;
    case "5":
        start = 'unknown';
        end = 'unknown';
    default:
        start = 'error';
        end = 'error';
        break;
}

currentPatient['birthdate_start']= start;
currentPatient['birthdate_end']= end;

updateCurrentPatient(currentPatient);
